import React from 'react';
import './App.css';

// const promise = fetch('https://jsonplaceholder.typicode.com/users');

// promise
// .then(function(response) {  // fetch が返した Promise の解決を待つ
//   return response.text();
// })
// .then(function(text) {  // response.text が返した Promise の解決を待つ
//   console.log(text);
// });

let App = document.getElementById("name_box");

function getJson(){
    fetch("https://jsonplaceholder.typicode.com/users/")
        .then(response => response.json())
        //response.json()をusersとして扱います
        .then(users => {

        //取得したユーザー情報をイテレートしてhtmlに出力
        for(user of users){
            var list = document.createElement('li');
            list.innerHTML = user.name;
            App.appendChild(list);
        }
    });
};

getJson();

export default App;
