//   return response.text();
// })
// .then(function(text) {  // response.text が返した Promise の解決を待つ
//   console.log(text);
// });

function App() {
  return (
    <div className="App">
      <header className="App-header">

        <p>RestAPIのデータを表示するreact + tsアプリケーション</p>

        <a
          className="App-link"
          href="https://jsonplaceholder.typicode.com/users"
          target="_blank"
          rel="noopener noreferrer"
        >
          https://jsonplaceholder.typicode.com/users
        </a>

        <script>

        </script>



      </header>

    </div>

  );
}

const api = 'https://jsonplaceholder.typicode.com/users'
fetch(api)
  .then(res => res.json())
  .then(data => a(data))


// 結果を表示 --- (*2)
function a(data: any[]) {
  let s = ''
  const b = data[3] 

  for (let c of b) {
    console.log(c) // デバッグ表示
    s += 
      <>
      <h3>{c['id']}の天気</h3>
      <ul>
         <li>{c['name']}</li>
         <li>{c['email']}</li>
         <li>{c['address']}</li>
      </ul>
      </>  

  }
  document.write(s)
}

export default App;
