import React from 'react';

const api = 'https://jsonplaceholder.typicode.com/users'
fetch(api)
  .then(res => res.json())
  .then(id => memory(id))


// 結果を表示 --- (*2)
function memory(id: any[]) {
  let s = ''
  const b = id[3] 

  for (let c of b) {
    console.log(c) // デバッグ表示
    s += `
      <h3>{c['id']}</h3>
      <ul>
        <li>{c['name']}</li>
        <li>{c['email']}</li>
        <li>{c['address']}</li>
      </ul>  
    ` 
  }
  document.write(s)
}

export default memory;
